	function product(title, header, desc1, desc2, desc3, img){
		this.title = title;
		this.header = header;
		this.desc1 = desc1;
		this.desc2 = desc2;
		this.desc3 = desc3;
		this.img = img;
	}
	
		
	/* add progress bar based on product count, array of products returned from ajax */
	
	/*Run after ajax success callback:	*/
	addProgressBarsProducts();
	
	function addProgressBarsProducts(){
		for(var i = 1; i <= products.length; i++){
			i == 1 ? g = " active" : g = "";
			width = 100/products.length;
			$('.progress').append('<div class="progress-bar'+g+'" role="progressbar" style="width:'+width+'%"></div>');
		}
	}
	
	var productDisplay = 0;
	
	function progressProductSlide(t){ //true = forward; false = backwards;
		$('#productSection').animate({
			opacity: 0
			
		}, 600, function(){
			$('#productSection').promise().done(function(){
				if(t){
					productDisplay++;
				}else{
					productDisplay--;
				}
					
					$('#productSection').delay(300).animate({
						opacity: 1
			
					}, 500, function(){
						setProductInfo();
					});
				}
			);
		});
	}
	
	setProductInfo();
	
	function setProductInfo(){
		$('#productName b').html(products[productDisplay].title);
		$('#productHeader b').html(products[productDisplay].header);
		$('#desc1').html(products[productDisplay].desc1.replace(';',';<br/>'));
		$('#desc2').html(products[productDisplay].desc2.replace(';',';<br/>'));
		$('#desc3').html(products[productDisplay].desc3.replace(';',';<br/>'));
		$('.productImage').attr('src','assets/images/'+products[productDisplay].img);
	}
	
	function checkLimits(i, t){
		if(i == t){
			$('.nextProduct').addClass('disabled');
		}else if(i == 0){
			$('.previousProduct').addClass('disabled');
		}else{
			$('#fBody .disabled').removeClass('disabled');
		}
	}
	
